mapper.select:
select * 
from fileupload
where upload_User_Id=#{uploadUserId} and file_Real_Name=#{fileRealName} and parent_id=#{parentId} and file_Type=${@group.xuxiake.common.util.NetdiskConstant@FILE_IS_DIR} and file_Status=${@group.xuxiake.common.util.NetdiskConstant@FILE_STATUS_OF_NORMAL}

select f.*

  , CONCAT('/', fn_tree_pathname(f.parent_id, '/')) as pathname

from fileupload f
where
f.upload_User_Id=#{fileUpload.uploadUserId}
and f.file_Status = ${@group.xuxiake.common.util.NetdiskConstant@FILE_STATUS_OF_NORMAL}

  and parent_id=#{fileUpload.parentId}


  and file_Real_Name like  concat("%",#{fileUpload.fileRealName},"%")


  and

    file_type = #{fileType}


order by is_Dir, upload_Time desc
