mapper.select:
select

from sharefile
where share_Id = #{shareId}
and share_Status = ${@group.xuxiake.common.util.NetdiskConstant@SHARE_STATUS_OF_NORMAL}

select s.id, s.share_Id, s.share_Pwd, s.share_Time, s.access_times, s.download_times, s.save_times, f.file_save_name, f.file_real_name, f.file_size, f.file_type, f.is_Dir from sharefile s,fileupload f where s.share_user_id = #{userId} and s.share_status = ${@group.xuxiake.common.util.NetdiskConstant@SHARE_STATUS_OF_NORMAL} and s.file_id = f.id and f.file_status = ${@group.xuxiake.common.util.NetdiskConstant@FILE_STATUS_OF_NORMAL}
AND f.file_real_name LIKE CONCAT('%', #{fileName}, '%')
order by s.share_time desc
