mapper.select:
  select

  from x_user_file
	where
	user_id = #{userId}
	and parent_id=#{parentId}
	and status=${@group.xuxiake.common.util.NetdiskConstant@FILE_STATUS_OF_NORMAL}
	and is_dir=${@group.xuxiake.common.util.NetdiskConstant@FILE_IS_DIR}
	order by create_time desc

select

from
x_user_file
where
user_id = #{userId}
and file_name = #{fileName}
and parent_id = #{parentId}
and is_dir = ${@group.xuxiake.common.util.NetdiskConstant@FILE_IS_DIR}
and status = ${@group.xuxiake.common.util.NetdiskConstant@FILE_STATUS_OF_NORMAL}

select user_file.*, file_origin.file_ext_name, file_origin.file_size, IFNULL(file_origin.file_type, ${@group.xuxiake.common.util.NetdiskConstant@FILE_TYPE_OF_DIR}) file_type from x_user_file user_file LEFT JOIN x_file_origin file_origin ON user_file.origin_id = file_origin.id where user_file.status = ${@group.xuxiake.common.util.NetdiskConstant@FILE_STATUS_OF_NORMAL}
AND user_file.user_id = #{userFile.userId}
and user_file.parent_id = #{userFile.parentId}
and user_file.id = #{userFile.id}
and user_file.file_name like concat("%", #{userFile.fileName}, "%")
and
file_origin.file_type = #{fileType}
order by user_file.is_dir, user_file.update_time desc
